import styled from "styled-components";

export const ListContanier = styled/ul`
padding: 0;
cursor: poninter;
`
export const Row = styled.li`
display: flex;
align-items: center;
justify-content: space-between;
padding: 8px 0;
font-size: 1rem;
`

export const Text = styled.span`
::first-letter{
    text-transform: capitalize;
}
`
